import React, { useState, useEffect } from "react";
import Character from "./Character";
import "./style/List.css";
const List = () => {
  const [state, setState] = useState({
      loading: true,
      error: false,
      data:[]
  });

  let nextUrl = "https://rickandmortyapi.com/api/character";

  useEffect(() => {
    try{
        fetch("https://rickandmortyapi.com/api/character")
          .then((res) => res.json())
          .then((data) => {
            setState({
                loading: false,
                error: false,
                data: data.results
            });
          });
    }catch(err){
        setState({
            loading:false,
            error: true
        })
    }
  }, []);

  const loadMore = () => {
    fetch(nextUrl)
    .then(res => res.json())
    .then(data => {
        nextUrl = data.info.next;
        fetch(nextUrl)
        .then(res => res.json())
        .then(data => {
            
        })
    })
  }

  return (
    <div className="List" >
      {state.data.map((c) => {
        return <Character image={c.image} name={c.name} id={c.id} key={c.id} />;
      })}
      <button onClick={loadMore} >Next</button>
    </div>
  );
};

export default List;
